inline void poke(unsigned addr, byte value)
{
	*(volatile char *)addr = value;
}

inline byte peek(unsigned addr)
{
	return *(volatile char *)addr;
}

void draw_screen(const char *screen_data)
{
	byte *const Screen = (byte *)0x0400;

	for (int i = 0; i < 1000; i++)
		Screen[i] = screen_data[i];
}

void countdown_sequence()
{
	draw_screen(ready_screen);
	vic_waitFrames(60);
	draw_screen(three_screen);
	vic_waitFrames(60);
	draw_screen(two_screen);
	vic_waitFrames(60);
	draw_screen(one_screen);
	vic_waitFrames(60);
	draw_screen(go_screen);
	vic_waitFrames(60);
}

